import React,{useState} from "react";
import { FaBars, FaTimes , FaLinkedin, FaInstagram, FaGithub, FaVoicemail} from "react-icons/fa";
import { HiOutlineMail } from "react-icons/hi";
import { BsFillPersonLinesFill } from "react-icons/bs";
import logo from "../assets/logo.png";

const Navbar = () => {
  const [nav, setNav]=useState(false)
  const handleClick=() => setNav(!nav)
  
  return (
    <>
      <div className="chathura fixed w-full h-20 flex justify-between items-center px-4 bg-[#0EF6BE]">
        <div>
          <img
            src={logo}
            alt="logo png"
            
            style={{ width: "57px" }}
          />
        </div>

        <div className="text-4xl hidden md:flex">
          <ul className="flex">
            <li>Home</li>
            <li>About</li>
            <li>Skills</li>
            <li>Experience</li>
            <li>Contact</li>
          </ul>
        </div>

        <div id="hamburger" onClick={handleClick} className="md:hidden z-10">
          
          {!nav ? <FaBars/>: <FaTimes/>}
        </div>

        <div id="mobiles menu" className= {!nav ? "hidden": "absolute top-0 left-0 w-full h-screen bg-[#0EF6BE] flex flex-col justify-center items-center"}>
          <ul className="">
            <li className="py-6 text-5xl">Home</li>
            <li className="py-6 text-5xl">About</li>
            <li className="py-6 text-5xl">Skills</li>
            <li className="py-6 text-5xl">Experience</li>
            <li className="py-6 text-5xl">Contact</li>
          </ul>
        </div>

        <div className="flex fixed flex-col top-[35%] left-0">
          <ul>
            <li className="w-[160px] h-[60px] flex justify-between items-center">
              <a className="flex justify-between items-center w-full text-white" href="/">LinkedIn <FaLinkedin size={30}/> </a> 
               
            </li>
          </ul>

        </div>
      </div>
    </>
  );
};

export default Navbar;
