import React from 'react';
import {HiArrowNarrowRight} from 'react-icons/hi'
const [effect, setEffect] = useState(false);

const Home = () => {
  return (
    <div name='home' className='bg-[#1e2528] w-full h-screen'>    
      
    <div id='container' className='max-w-[1000px] mx-auto flex flex-col justify-center h-full'>
      <p className='text-8xl text-[#c0f4e8]'>Hello, </p>
      <p className='text-5xl text-slate-300'>I am <strong className='text-[#e2e6f4] text-9xl'>Omkar Kardile</strong></p>
      <p><h4 className='text-7xl text-[#bcbfc8] font-bold'>I'm a Designer. I like to design Ui/Ux, FrontEnd Web, etc.</h4></p>

      <div className='text-5xl text-[#0EF6BE]'>
        <button className='border-2 px-6 py-3 my-2 flex items-center border-white bg-[black] shadow-xl shadow-black hover:bg-[#3A504B] hover:border-[#0EF6BE]'>View More <HiArrowNarrowRight className='ml-3'/> </button> 
      </div>
      <div className="flex h-screen flex-col justify-center">
      <div className="flex justify-center">
      <button className='{`${
            effect && "animate-wiggle"
          } bg-blue-500 p-4 text-white rounded hover:bg-blue-700 hover:shadow-xl`}
          onClick={() => {
            setEffect(true);
          }}
          onAnimationEnd={() => setEffect(false)}'>Hi</button>
</div>
</div>

    </div>
    </div>
  )
}

export default Home
